import axios from 'axios';

export const BTC_UPDATE = 'BTC:BTCUpdate';

export function BTCUpdate( newData ) {
    return ({
        type: BTC_UPDATE,
        payload: {
            BTC : newData
        }
    })   
}

export function apiRequest (){ 
    return dispatch => {
        axios.get('https://api.cryptonator.com/api/full/btc-usd').
        then((res)=>{
            console.log(res);
            dispatch(BTCUpdate(res))
        })
    }
}
